# python 实现的行程长度压缩算法和解压缩算法
def slc(s):
    s_list = []
    i = 0
    while i<len(s):
        counter = 0
        c = s[i]
        k = c
        if 48 <= ord(k) <= 57:
            k = chr(97 + ord(k) - 48)
        else:
            k = chr(ord(k) + 10)
        if i < len(s) - 1:
            for j in range(i+1, len(s)):
                if c != s[j]:
                    counter = j
                    break
            else:
                counter = j+1
            if counter > i+1:
                s_list.append(str(counter-i) + k)
            else:
                s_list.append(k)
            i = counter
        else:
            s_list.append(k)
            i = len(s)
    return ''.join(s_list)

def deslc(s):
    s_list = []
    i = 0
    while i < len(s):
        n = i
        for j in range(i, len(s)):
            if 97 <= ord(s[j]):
                n = j
                break
        if n == i:
            c = 1
        else:
            c = int(s[i: n])
        ss = s[n]
        if ord(ss) - 97 < 10:
            ss = str(ord(ss) - 97)
        else:
            ss = chr(ord(ss) - 10)
        s_list.append(ss*c)
        i = n + 1
    return ''.join(s_list)

if __name__ == "__main__":
    s = ''
    # s = '0000001e00'
    # s = '0000001'
    # s = '00'
    print(slc(s))
    s = ''
    print(deslc(s))